sys_script_client.script:
function onSubmit() 
{
   var startdate=g_form.getValue('course_start_date');
   var enddate=g_form.getValue('course_end_date');
	var cdate=new Date();
	var Sndate=cdate.getFullYear()+'-'+('0'+cdate.getMonth()+1).slice(-2)+'-'+('0'+cdate.getDate()).slice(-2);

	if(enddate<startdate||enddate==startdate)
		{
			g_form.addErrorMessage("Invalid EndDate:Select Appropriate EndDate to Proceed Further");
			alert('Selcet appropriate EndDate\n'+
			'Conditions:\n'+
			'1.End date should not less than start date\n'+
			'2.End date should not equal to start date');
			return false;
		}
	else if(startdate<=Sndate)
		{

			g_form.addErrorMessage('Inavlid StartDate: Start date Should be Today Or Any Date from today!');
			alert('Selcet appropriate StartDate\n'+
			'Conditions:\n'+
			'Start date should be TodayDate  Or Any Date from today!');
			return false;
		}


}
