sys_script_include.script:
var GetEmail = Class.create();
GetEmail.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {

getmail:function()
	{
		var requestor=this.getParameter('sysparm_u_requested_for');
		var GrNeedit=new  GlideRecord('sys_user');
		GrNeedit.addQuery('sys_id',requestor);
		GrNeedit.query();
		if(GrNeedit.next())
			{
				gs.log(GrNeedit.email);
				return GrNeedit.email;
			}

	},
    type: 'GetEmail'
});
